sp_widget.client_script:
function($scope, $rootScope) {
	/* widget controller */
	var c = this;
	c.note = {};

c.updateNote = function(updateType) {
		c.server.get({
			action: "updateNote",
			noteId: c.note.noteId,
			title: c.note.title,
			note: c.note.note
		}).then(function(r) {
			var noteUpdates = {
				id: c.note.noteId,
				field: updateType,
				value: c.note[updateType]
			};
			$rootScope.$emit('updateNote', noteUpdates);
		})
	}

	$rootScope.$on('selectNote', function(event, data) {
		c.server.get({
			action: "getNote",
			noteId: $rootScope.noteId
		}).then(function(r) {
			c.note.title = r.data.note.title;
			c.note.note = r.data.note.note;
			c.note.noteId = r.data.note.sys_id;
		});
	});

}

sp_widget.script:
(function() {
	/* populate the 'data' object */
	/* e.g., data.table = $sp.getValue('table'); */
	if (input && input.noteId) {
		var noteGR = new GlideRecord('x_snc_createnotes_note');
		if (noteGR.get(input.noteId)) {
			if (input.action === "getNote") {
				data.note = {};																														 
				$sp.getRecordValues(data.note, noteGR, "title, note, sys_id");
			} else if (input.action == 'updateNote') { 
				noteGR.title = input.title;
				noteGR.note = input.note;
				noteGR.update();
			}			
		}
	}
})();
